import CartContext from "./cart-context";
import { useReducer } from "react";

const defaultCartState = {
  items: [],
  totalAmount: 0,
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case "ADD_ITEM":
      const updatedTotal = state.totalAmount + action.items.price * action.items.amount;
      const existingCartItemIndex = state.items.findIndex((item) => item.id === action.items.id);
      const existingCartItem = state.items[existingCartItemIndex];
      
      let updatedItems;
      if(existingCartItem){
        let updatedItem;
          updatedItem = {
                ...existingCartItem,
                amount: existingCartItem.amount + action.items.amount,
          };
          updatedItems = [...state.items];
          updatedItems[existingCartItemIndex] = updatedItem;
      } else{
          updatedItems = state.items.concat(action.items);
      }
      if(action.type === '')
      return {
        items: updatedItems,
        totalAmount: updatedTotal,
      };
    case "REMOVE_ITEM":
      return {
        ...state,
        items: state.items.filter((item) => item.id !== action.items),
        totalAmount: state.totalAmount - action.items.price,
      };
    case "REMOVE_ALL_ITEMS":
      return {
        ...state,
        items: [],
        totalAmount: 0,
      };
    default:
      return state;
  }
};

const CartProvider = (props) => {
  const [state, dispatch] = useReducer(cartReducer, defaultCartState);
  const addItemHandler = (item) => {
    dispatch({
      type: "ADD_ITEM",
      items: item,
    });
  };
  const removeItem = (id) => {
    dispatch({
      type: "REMOVE_ITEM",
      items: id,
    });
  };
  const cartContext = {
    items: state.items,
    totalAmount: state.totalAmount,
    addItem: addItemHandler,
    removeItem: removeItem,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
